mapper.update:
update announcement
set
  Announcement_State = 1,
  PublishTime = #{publishtime,jdbcType=TIMESTAMP}
where Announcement_Id = #{announcementId,jdbcType=INTEGER}

update announcement
set
  Announcement_State = 2,
  SuspendTime = #{suspendtime,jdbcType=TIMESTAMP}
where Announcement_Id = #{announcementId,jdbcType=INTEGER}

update announcement

  TimeLiness = 1,
  DeleteName = #{deletename,jdbcType=VARCHAR},
  DeleteTime = #{deletetime,jdbcType=TIMESTAMP}

where Announcement_Id = #{announcementId,jdbcType=INTEGER}

update announcement
AnnouncementType_Id = #{announcementtypeId,jdbcType=INTEGER}, Announcement_Theme = #{announcementTheme,jdbcType=VARCHAR}, Announcement_Content = #{announcementContent,jdbcType=VARCHAR}, Start_Time = #{startTime,jdbcType=TIMESTAMP}, End_Time = #{endTime,jdbcType=TIMESTAMP}, UpdateName = #{updatename,jdbcType=VARCHAR}, UpdateTime = #{updatetime,jdbcType=TIMESTAMP}
where Announcement_Id = #{announcementId,jdbcType=INTEGER}
